int CalculateFinalFloor (string pInput)
{
    //Simply iterate over all instructions, summing the floor changes they represent
    return pInput.Sum(r => r == '(' ? 1 : -1);
}

int CalculateFirstTimeBasementHit (string pInput)
{
    int floor = 0;
    int charIndex = 1;

    //follow the floor change instructions and exit when we hit the basement
    foreach (char c in pInput)
    {
        //Alternative to part 1 to calculate -1 or 1 based on ( or )
        floor += 1 - (c - '(') * 2;

        //Exit the moment we hit the basement
        if (floor == -1) return charIndex;

        charIndex++;
    }

    return -1;
}

