void Part1_CalculateFinalFloor (string pInput)
{
    //Simply iterate over all instructions, summing the floor changes they represent
    Console.WriteLine("Part 1 (Final floor):" + pInput.Sum(r => r == '(' ? 1 : -1));
}

void Part2_CalculateFirstTimeBasementHit (string pInput)
{
    int floor = 0;
    int charIndex = 1;

    foreach (char c in pInput)
    {
        //Alternative to part 1 to calculate -1 or 1 based on ( or )
        floor += 1 - (c - '(') * 2; 
        
        //Exit the moment we hit the basement
        if (floor == -1) { 
            Console.WriteLine("Part 2 (First time basement hit):" + charIndex);
            break;
        }

        charIndex++;
    }
}

