void Part1 (string pInput)
{
    Console.WriteLine("Part 1:" + pInput.Sum(r => r == '(' ? 1 : -1));
}

void Part2 (string pInput)
{
    int floor = 0;
    int charIndex = 1;

    foreach (char c in pInput)
    {
        //Alternative to part 1 to calculate -1 or 1 based on ( or )
        floor += 1 - (c - '(') * 2; 
            
        if (floor == -1) { 
            Console.WriteLine("Part 2:" + charIndex);
            break;
        }

        charIndex++;
    }
}

